    let b = query[1];
    let r = i.split('/');
    let s = r[r.length - 1].replace(/\.(flv|m3u8)/g, '');
    let c = b.split('&', 4);
    let n = {};

    for (let i of c) {
        if (i != '') {
            let pair = i.split('=');
            n[pair[0]] = pair[1];
        }
    }
    let fm = unescape(n['fm']);
    let u = base64Decode(fm);
    let p = u.split('_')[0];
    let f = (new Date()).valueOf()*1000+'';
    let l = n['wsTime'];
    let t = '0';
    let h = [p, t, s, f, l].join('_');
    let m = md5(h);
    let y = c[c.length - 1];
    let url = i + "?wsSecret=" + m + "&wsTime=" + l + "&u=" + t + "&seqid=" + f + "&" + y;
